package com.tnsif.loops;

public class EachLoopMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {101,202,303,404,505};	
		//for integer array
				for(int i:a)
				{
				System.out.println(i);	
				}
				
				char ch[]= {'j','a','v','a'};
				for(char c:ch)
				{
					System.out.print(c);		
				}
				System.out.println(" ");
				String s1[]= {"Java","programming","learning"};
		//for String array
				for(String s:s1)
				{
					System.out.print(s+" ");		
				}


	}

}
